import React, { useEffect, useState } from "react";
import { Carousel } from ".";
import axios from "axios";
import { API_URL } from "../constant";

const { WISHES_URL } = API_URL;

const Wishes = () => {
  // const images = [
  //   "https://images.unsplash.com/photo-1506501139174-099022df5260?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&auto=format&fit=crop&w=1351&q=80",
  //   "https://images.unsplash.com/photo-1523438097201-512ae7d59c44?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&auto=format&fit=crop&w=1350&q=80",
  //   "https://images.unsplash.com/photo-1513026705753-bc3fffca8bf4?ixlib=rb-1.2.1&ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&auto=format&fit=crop&w=1350&q=80",
  // ];
  const [wishes, setWishes] = useState();
  const [form, setForm] = useState();
  const fetchWishes = async () => {
    try {
      const req = await axios.get(WISHES_URL);
      if (req) {
        const { data } = req?.data;
        setWishes(data);
      }
    } catch (error) {
      console.log(error);
    }
  };

  const postWishes = async () => {
    const request = {};
    try {
      const res = await axios.post();
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    fetchWishes();
  }, []);

  return (
    <section
      id="wishes"
      className="py-8 mb-32 font-cormorant-sc overflow-x-hidden"
    >
      <div className="w-full px-4 md:w-1/3 grid gap-6 md:text-lg mb-16 mx-auto">
        <h2 className="text-center md:text-lg sm:text-xs lg:text-3xl antialiased sm:subpixel-antialiased md:antialiased font-semibold">
          Wishes
        </h2>
        <p className="text-left">
          Yes, you can still send us wishes! Make sure you write down your name
          so that all of us can recognize you easily ^_^
        </p>
        <form className="grid gap-4" onSubmit={(e) => postWishes(e)}>
          <div className="grid gap-2">
            <label htmlFor="wishes_name" className="font-medium text-left">
              Name
            </label>
            <input
              className="bg-white py-2 px-4 outline-none border border-linen focus:border-linen-darker"
              spellCheck="false"
              id="wishes_name"
              type="text"
            ></input>
          </div>
          <div className="grid gap-2">
            <label htmlFor="wishes_wishes" className="font-medium text-left">
              Wishes for Putri &amp; Arif
            </label>
            <textarea
              className="
      bg-white
      py-2
      px-4
      outline-none
      border border-linen
      focus:border-linen-darker
      h-36
    "
              type="text"
              spellCheck="false"
              id="wishes_wishes"
            ></textarea>
          </div>
          <div className="w-full grid sm:block sm:text-right">
            <button
              type="submit"
              disabled=""
              className="py-2 px-4 text-white font-bold bg-yellow-600 cursor-not-allowed"
            >
              SEND WISHES
            </button>
          </div>
        </form>
      </div>
      <Carousel wishes={wishes} />
    </section>
  );
};

export default Wishes;
