import { useEffect, useState } from "react";
import dayjs from "dayjs";

const Countdown = () => {
  const [timeLeft, setTimeLeft] = useState(calculateTimeLeft());

  const calculateTimeLeft = () => {
    const weddingDate = dayjs("2021-10-23");
    const difference = weddingDate.diff(dayjs());
    let timeLeft = {};

    if (difference > 0) {
      timeLeft = {
        days: Math.floor(difference / (1000 * 60 * 60 * 24)),
        hours: Math.floor((difference / (1000 * 60 * 60)) % 24),
        minutes: Math.floor((difference / 1000 / 60) % 60),
        seconds: Math.floor((difference / 1000) % 60),
      };
    }

    return timeLeft;
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setTimeLeft(calculateTimeLeft());
    }, 1000);
  });

  return (
    <section class="batik-deco">
      <section
        class="
            py-10
            sm:py-24
            md:py-48
            xl:py-56
            grid grid-flow-col
            auto-rows-max auto-cols-max
            gap-4
            md:gap-16
            justify-center
            px-16
            grad-trans-linen-trans
          "
      >
        <div class="bg-linen bg-opacity-70 p-2 sm:p-3 sm:py-5 md:py-10 text-gray-700 w-12 sm:w-20 md:w-28 transition-all">
          <p class="font-gilda text-lg sm:text-3xl md:text-5xl">00</p>
          <p class="text-2xs sm:text-xs md:text-base">DAYS</p>
        </div>
        <div class="bg-linen bg-opacity-70 p-2 sm:p-3 sm:py-5 md:py-10 text-gray-700 w-12 sm:w-20 md:w-28 transition-all">
          <p class="font-gilda text-lg sm:text-3xl md:text-5xl">00</p>
          <p class="text-2xs sm:text-xs md:text-base">HOURS</p>
        </div>
        <div class="bg-linen bg-opacity-70 p-2 sm:p-3 sm:py-5 md:py-10 text-gray-700 w-12 sm:w-20 md:w-28 transition-all">
          <p class="font-gilda text-lg sm:text-3xl md:text-5xl">00</p>
          <p class="text-2xs sm:text-xs md:text-base">MINUTES</p>
        </div>
        <div class="bg-linen bg-opacity-70 p-2 sm:p-3 sm:py-5 md:py-10 text-gray-700 w-12 sm:w-20 md:w-28 transition-all">
          <p class="font-gilda text-lg sm:text-3xl md:text-5xl">00</p>
          <p class="text-2xs sm:text-xs md:text-base">{distance}SECONDS</p>
        </div>
      </section>
    </section>
  );
};

export default Countdown;
